mapper.insert:
INSERT INTO post VALUE (
    NULL, #{title}, #{content}, NOW(),
    NULL, #{user.id}, #{category.id},
    #{hidden}, 0, 0,
    0, #{reward}
)

INSERT INTO post VALUE (
    NULL, #{title}, #{content}, NOW(),
    NULL, #{user.id}, #{category.id},
    #{hidden}, #{deleted}, #{excellent},
    #{top}, #{reward}
    )

mapper.update:
UPDATE post
SET is_hidden = #{state}
WHERE id = #{id}

UPDATE post
SET is_deleted = 1
WHERE id = #{id}

UPDATE post
SET is_top = #{state}
WHERE id = #{id}

UPDATE post
SET is_excellent = #{state}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM post
WHERE is_deleted = 0

    AND category_id = #{categoryId}


    AND user_id = #{userId}


    AND is_hidden = #{hidden}

SELECT COUNT(*) FROM post
WHERE is_top = 1 AND is_hidden = 0 AND is_deleted = 0

    AND category_id = #{categoryId}

SELECT *
FROM post
WHERE is_deleted = 0

    AND category_id = #{categoryId}
    AND is_hidden = 0


    AND user_id != #{userId}


    AND is_top = #{top}
    AND is_hidden = 0

ORDER BY create_time DESC
LIMIT #{from}, #{num}
